    const style: React.CSSProperties = {}

    const editorTheme = this.props.args["theme"];
    const defaultTheme = theme? theme.base : "dark";
    var themeProp = "streamlit_dark";
    switch (editorTheme) {
      case "contrast":
        themeProp = (defaultTheme === "dark")? "streamlit_light" : "streamlit_dark";
        break;
      case "light":
        themeProp = "streamlit_light";
        break;
      case "dark":
