          commands={this.commands}
          {...aceProps}
        />
        <StyledButton 
          key="copy-default" 
          theme={{top: "8px", right: "0.3rem", left: style.left, bottom: style.bottom}}
          onClick={this.copyToClipboard} >
           <CopyIcon size="16" />
        </StyledButton>       
        {this.customButtons.regular.map( (button, index) => (
          <StyledButton 
            key={button.name? button.name : "regButton" + index} 
            theme={button.position? button.position : {}} 
            onClick={(button.commands || button.response)? () => this.executeAll(button.commands, button.response) : this.copyToClipboard}>
              {creatIcon(Object.keys(Icons).includes(button.feather)?button.feather as iconKey: "Copy")}
          </StyledButton>
         ))}
        {this.customButtons.primary.map( (button, index) => (
          <StyledPlayButton 
            key={button.name? button.name : "primButton" + index} 
            theme={button.position? button.position : {}} 
            onClick={(button.commands || button.response)? () => this.executeAll(button.commands, button.response) : this.copyToClipboard}>
              {creatIcon(Object.keys(Icons).includes(button.feather)?button.feather as iconKey: "Copy")}
