import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import styled from "styled-components"
import React, { ReactNode } from "react"
import { Play as PlayIcon, Copy as CopyIcon } from "react-feather"
import AceEditor from "react-ace";
import ace, { UndoManager } from "ace-builds";

import "ace-builds/webpack-resolver";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-streamlit_dark";
import "ace-builds/src-noconflict/theme-streamlit_light";
import "ace-builds/src-noconflict/ext-language_tools";
//import "ace-builds/src-noconflict/ext-keybinding_menu";
//import "ace-builds/src-noconflict/keybinding-vscode";
//import { relative } from "path";
//import { Editor } from './streamlit-ace-build/ace';

interface State {
  code: string
  lang: string
  isFocused: boolean
  undoManager: object
  cursorPosition: object
}

const defaultOptions = {
  maxLines: 12,
  minLines: 12,
  fontFamily: '"Source Code Pro", monospace',
  cursorStyle: "smooth",
  displayIndentGuides: false,
  wrap: true,
  highlightActiveLine: true,
  showPrintMargin: false,
  showLineNumbers: false,
  foldStyle: "markbegin",
  autoScrollEditorIntoView: true,
  animatedScroll: true,
  fadeFoldWidgets: true,
}

const defaultEditorProps = {
  editorProps: {
    $blockScrolling: true
  }
}

const defaultProps = {
  cursorStart: 1,
  editorProps: defaultEditorProps,
  enableBasicAutocompletion: false,
  enableLiveAutocompletion: true,
  enableSnippets: true,
  focus: false,
  fontSize: 14,
  highlightActiveLine: true,
  navigateToFileEnd: true,
  placeholder: null,
  readOnly: false,
  scrollMargin: [15,15,0,0],
  setOptions: defaultOptions,
  showGutter: true,
  showPrintMargin: false,
  style: {},
  tabSize: 4,
  width:"auto",
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class CodeEditor extends StreamlitComponentBase<State> {
  private defaultUndoManager = new UndoManager();
  public state = {code: this.props.args['code'], lang: this.props.args['lang'], isFocused: false, cursorPosition: {row: 0, column: 1}, undoManager: this.defaultUndoManager}

  public respond = (responseType = "submit") => {
    console.log("respond called")
    this.setState(this.state, () => {Streamlit.setComponentValue({ type: responseType, code: this.state.code});});
  }

  public copyToClipboard = async (): Promise<void> => {
    try {
      await navigator.clipboard.writeText(this.state.code);
      console.log(this.state.code);
    } catch (err) {
      console.warn('Failed to copy securely -> reverting to unsecure copy');
      this.unsecureCopyToClipboard();
    }
  }

  private unsecureCopyToClipboard = () => {
    const textField = document.createElement('textarea');
    textField.value = this.state.code;
    document.body.appendChild(textField);
    textField.select();
    document.execCommand('copy');
    textField.remove();
  }

  private createSnippets = (snippets: object | [object]) : string => {
    return( (Array.isArray(snippets) ? snippets : [snippets]).map(({ name, code }) =>
        ([
            'snippet ' + name,
            code.split('\n')
                .map((c: string) => '\t' + c)
                .join('\n'),
        ].join('\n'))
    ).join('\n'))
  }

  public render = (): ReactNode => {
    // // Arguments that are passed to the plugin in Python are accessible
    // // via `this.props.args`. Here, we access the "name" arg.
    // const code = this.props.args["code"]

    const aceEditorProps = {...defaultEditorProps, ...this.props.args['editorProps']};
    const aceOptions = {...defaultOptions, ...this.props.args['options']}
    const partProps = {setOptions: aceOptions, editorProps: aceEditorProps}
    const aceProps = {...defaultProps, ...partProps, ...this.props.args['props']}


    console.log("Render----------------");
    // Streamlit sends us a theme object via props that we can use to ensure
    // that our component has visuals that match the active theme in a
    // streamlit app.
    console.log(this.state.lang)

    const { theme } = this.props
    const style: React.CSSProperties = {}

    const editorTheme = this.props.args["theme"];
    const defaultTheme = theme? theme.base : "dark";
    var themeProp = "dark";
    switch (editorTheme) {
      case "contrast":
        themeProp = (defaultTheme === "dark")? "streamlit_light" : "streamlit_dark";        break;
      case "light":
        themeProp = "streamlit_light";
        break;
      case "dark":
        themeProp = "streamlit_dark"
        break;
      default:
        themeProp = (defaultTheme === "dark")? "streamlit_dark" : "streamlit_light";
        break;
    }

    console.log(theme?.base)

    // Maintain compatibility with older versions of Streamlit that don't send
    // a theme object.
    // if (theme) {
    //   // Use the theme object to style our button border. Alternatively, the
    //   // theme style is defined in CSS vars.
    //   style.stroke = theme.primaryColor
    // }
    style.border = "none"
    style.outline = "none"
    style.top = "none"
    style.bottom = "none"
    style.left = "none"
    style.right = "none"
    style.stroke = theme? theme.primaryColor : "rgba(250,250,250,1)"

    const StyledCodeEditor = styled.div(() => ({
      height: "5rem",
      borderRadius: "4px",
      overflow: "visible",
      ":hover": {
        button: {
          opacity: 1,
          transform: "scale(1)",
          transition: "none",
        },
      },
    }))

    // const StyledIconContainer = styled.div(({theme}) => ({
    //   position: "relative",
    //   left: "calc(-2.5rem - 0.5rem)",
    //   width: "calc(100% + 2.5rem + 0.5rem)",
    //   display: "flex",
    //   alignItems: "center",
    //   overflow: "visible",
    //   ":hover": {
    //     a: {
    //       opacity: 1,
    //       transform: "scale(1)",
    //       transition: "none",
    //     },
    //   },
    // }))

    const StyledButton = styled.button(({theme}) => ({
      // center icon
      lineHeight: 0,
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      // copied from full screen button
      transform: "scale(0)",
      transition: "opacity 300ms 150ms, transform 300ms 150ms",
      opacity: 0,
      position: "absolute",
      top: theme.top,
      left: theme.left,
      right: theme.right,
      bottom: theme.bottom,
      //color: "rgba(250,250,250,0.6)",
      height: "2.5rem",
      width: "2.5rem",
      zIndex: 101,
      border: "none",
      borderRadius: "50%",
      background: "none",
      ":focus ": {
        outline: "none",
      },
      svg: {
        stroke: "rgba(250,250,250,0.6)",
      }, 
      ":hover svg": {
        stroke: "rgba(250,250,250,1)",
      },
    }));

    const StyledPlayButton = styled(StyledButton)({
      ":hover svg": {
        stroke: style.stroke,
      },
    });

    return (
      <StyledCodeEditor>
        <StyledButton theme={{top: "8px", right: "0.3rem", left: style.left, bottom: style.bottom}} onClick={this.copyToClipboard}>
          <CopyIcon size="16" />
        </StyledButton>
        <AceEditor
          name="UNIQUE_ID_OF_DIV"
          mode={this.state.lang}
          theme={themeProp}
          keyboardHandler={this.props.args["shortcuts"]}
          value={this.state.code}
          onBeforeLoad={(ace) => {
            console.log("~before load~");
            console.log(this.state);
            //console.log(ace);
            console.log("~~~~~~~~~~~~~")
          }}
          onLoad={(editor) => {
            console.log("~on load~");
            console.log("------editor contents------");
            console.log(editor.getValue());
            console.log("---------------------------");

            // console.log(typeof(editor));

            editor.setOptions({
              enableBasicAutocompletion: true,
              enableSnippets: true,
            })

            const snippets = this.createSnippets(this.props.args['snippets']);
            if(snippets){
              const snippetManager = ace.require('ace/snippets').snippetManager;

              //// The function getMode() returns an Ace.SyntaxId object that
              //// has no way to get or access its $id variable. So the following
              //// doesnt work:
              //const id = editor.session.getMode().$id;

              //// The current workaround used here is to recognize that when the 
              //// AceEditor mode prop is set with a string (ex: mode="python"), it  
              //// constructs the mode id as follows: setMode(`ace/mode/${mode}`)
              const id = `ace/mode/${this.state.lang}`;

              const m = snippetManager.files[id]

              // testing
              // const snippets = this.createSnippets([{ name: 'build', code: 'console.log("build")' },
              //                                       { name: 'destroy', code: 'console.log("destroy")' },
              //                                       ]);

              snippetManager.register(snippetManager.parseSnippetFile(snippets, this.state.lang), this.state.lang)

            }

            editor.getSession().setUndoManager(this.state.undoManager);

            const cursorPos = this.state.cursorPosition;
            console.log(cursorPos);
            editor.moveCursorTo(cursorPos.row, cursorPos.column);
            editor.renderer.scrollCursorIntoView(cursorPos, 0.5);
            //editor.renderer.setScrollMargin(15,15,0,0);
            //editor.resize();
          }}
          onBlur={(e, editor) => {
            console.log("~~onBlur~~")           
            //console.log(editor? "autosave session success" : "autosave session failed");
            // console.log(this.state);
            // console.log("calling setState...");
            this.setState({code: editor?.getValue() || this.state.code, cursorPosition: editor?.getCursorPosition() || this.state.cursorPosition, undoManager: editor?.getSession().getUndoManager() || this.state.undoManager, lang: this.state.lang, isFocused: this.state.isFocused});
