import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"
import AceEditor from "react-ace";
import { UndoManager } from "ace-builds";

import { Play as PlayIcon, Copy as CopyIcon } from "react-feather"
//import UndoManager from "react-ace"

import styled from "styled-components"

import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-one_dark";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/keybinding-vscode";
//import { relative } from "path";

interface State {
  code: string
  lang: string
  isFocused: boolean
  undoManager: object
  cursorPosition: object
}

// /** Focus handler for our "Click Me!" button. */
// function onChange(value: string, event?: any){
//   console.log("change", value);
// }

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class MyComponent extends StreamlitComponentBase<State> {
  private defaultUndoManager = new UndoManager();
  public state = {code: "", lang: "python", isFocused: false, cursorPosition: {row: 0, column: 1}, undoManager: this.defaultUndoManager}

  //reactAceComponent = this.refs.reactAceComponent;

  public submit = () => {
    Streamlit.setComponentValue({ type: "submit", code: this.state.code});
  }

  public copyToClipboard = async (): Promise<void> => {
    try {
      await navigator.clipboard.writeText(this.state.code);
      console.log('Code copied to clipboard');
      console.log('-------------------------');
      console.log(this.state.code);
    } catch (err) {
      console.error('Failed to copy code securely -> reverting to unsecure copy');
      this.unsecureCopyToClipboard();

    }
  }

  private unsecureCopyToClipboard = () => {
    const textField = document.createElement('textarea');
    textField.innerText = this.state.code;
    document.body.appendChild(textField)
    //textField.focus({preventScroll:true})
    textField.select()
    document.execCommand('copy')
    textField.remove()
  }

  public render = (): ReactNode => {
    // // Arguments that are passed to the plugin in Python are accessible
    // // via `this.props.args`. Here, we access the "name" arg.
    // const code = this.props.args["code"]

    console.log("~render~");
    // Streamlit sends us a theme object via props that we can use to ensure
    // that our component has visuals that match the active theme in a
    // streamlit app.
    const { theme } = this.props
    const style: React.CSSProperties = {}

    // this.state.code = this.props.args["code"];
    // this.state.lang = this.props.args["lang"]

    console.log(theme)

    // Maintain compatibility with older versions of Streamlit that don't send
    // a theme object.
    if (theme) {
      // Use the theme object to style our button border. Alternatively, the
      // theme style is defined in CSS vars.
      const borderStyling = `1px solid ${
        this.state.isFocused ? theme.primaryColor : "gray"
      }`
      style.border = borderStyling
      style.outline = borderStyling
      style.top = "none"
      style.bottom = "none"
      style.left = "none"
      style.right = "none"
    }

    const StyledCodeEditor = styled.div(() => ({
      height: "5rem",
      borderRadius: "4px",
      overflow: "visible",
      ":hover": {
        button: {
          opacity: 1,
          transform: "scale(1)",
          transition: "none",
        },
      },
    }))

    // const StyledIconContainer = styled.div(({theme}) => ({
    //   position: "relative",
    //   left: "calc(-2.5rem - 0.5rem)",
    //   width: "calc(100% + 2.5rem + 0.5rem)",
    //   display: "flex",
    //   alignItems: "center",
    //   overflow: "visible",
    //   ":hover": {
    //     a: {
    //       opacity: 1,
    //       transform: "scale(1)",
    //       transition: "none",
    //     },
    //   },
    // }))

    const StyledIcon = styled.button(({ theme }) => ({
      // center icon
      lineHeight: 0,
      display: "flex",
      alignItems: "center",
      justifyContent: "center",

      // copied from full screen button
      transform: "scale(0)",
      transition: "opacity 300ms 150ms, transform 300ms 150ms",
      opacity: 0,
      position: "absolute",
      top: theme.top,
      left: theme.left,
      right: theme.right,
      bottom: theme.bottom,
      //color: "rgba(250,250,250,0.6)",
      height: "2.5rem",
      width: "2.5rem",
      zIndex: 101,
      border: "none",
      borderRadius: "50%",
      background: "none",
      ":focus ": {
        outline: "none",
      },
      svg: {
        stroke: "rgba(250,250,250,0.6)",
      }, 
      ":hover svg": {
        stroke: "rgba(250,250,250,1)",
      },
    }))

    return (
      <StyledCodeEditor>
        <StyledIcon theme={{top: "8px", right: "0.3rem"}} onClick={this.copyToClipboard}>
          <CopyIcon size="15" />
        </StyledIcon>
        <AceEditor
          mode={this.state.lang}
          theme="one_dark"
          keyboardHandler="vscode"
          value={this.state.code}
          cursorStart={1}
          fontSize={14} 
          width="auto"
          onBeforeLoad={(editor: object) => {
            console.log("~before load~");
          }}
          onLoad={(editor: any) => {
            console.log("~on load~");
            console.log(editor);
            console.log(typeof(editor));

            editor.getSession().setUndoManager(this.state.undoManager);

            const cursorPos = this.state.cursorPosition;
            console.log(cursorPos);
            editor.moveCursorTo(cursorPos.row, cursorPos.column);
            editor.renderer.scrollCursorIntoView(cursorPos, 0.5);
            //editor.renderer.setScrollMargin(15,15,0,0);
            editor.resize();
          }}
          name="UNIQUE_ID_OF_DIV"
          scrollMargin={[15,15,0,0]}
          editorProps={{ 
            $blockScrolling: true
          }}
          setOptions={{
            maxLines: 10,
            minLines: 10,
            fontFamily: '"Source Code Pro", monospace',
            cursorStyle: "smooth",
            displayIndentGuides: false,
            wrap: true,
            highlightActiveLine: true,
            showPrintMargin: false,
            showLineNumbers: false,
            foldStyle: "markbegin",
            enableLiveAutocompletion: true
          }}
          commands={[{   // commands is array of key bindings.
            name: 'submit', //name for the key binding.
            bindKey: {win: 'Ctrl-Enter', mac: 'Command-Enter'}, //key combination used for the command.
            exec: (editor) => {
               console.log("submit keys pressed")

               // IMPORTANT: The function `setSate()` is required to make sure component state is saved before submit.
               //            Otherwise changes to the editor and its contents will be lost.
               this.setState({code: editor.getValue(), cursorPosition: editor.getCursorPosition(), undoManager: editor.getSession().getUndoManager()});
               console.log("state is set, now submitting...")
               this.submit(); //function to execute when keys are pressed.
          }}, {
            name: 'save-state',
            bindKey: {win: 'Ctrl-Alt-S', mac: 'Command-Alt-S'},
            exec: (editor) => {
               console.log("save state keys pressed")
               //console.log(typeof(editor));
               this.state = {code: editor.getValue(), cursorPosition: editor.getCursorPosition(), undoManager: editor.getSession().getUndoManager(), lang: this.state.lang, isFocused: this.state.isFocused};
            }
          }]}
          onBlur={(e, editor) => {
            console.log("Lost focus")           
            console.log(editor? "autosave session success" : "autosave session failed");

            this.state = {code: editor?.getValue() || this.state.code, cursorPosition: editor?.getCursorPosition() || this.state.cursorPosition, undoManager: editor?.getSession().getUndoManager() || this.state.undoManager, lang: this.state.lang, isFocused: this.state.isFocused};
          }}
        />
        <StyledIcon theme={{bottom: "6px", right: "0.3rem"}} onClick={this.submit}>
          <PlayIcon size="15" />
        </StyledIcon>
      </StyledCodeEditor>
    )
  }

  public componentDidMount(): void {
    console.log("~mounted~");
    this.setState({code: this.props.args["code"]});
    super.componentDidMount();
  }

  public componentDidUpdate(): void {
    console.log("~updated~");
    super.componentDidUpdate();
  }
}

  //==========================================================================
  // Extra snippets of code
  //   /** Blur handler for our "Click Me!" button. */
  //   private _onBlur = (): void => {
  //     this.setState({ isFocused: false })
  //   }
  // }

    // /** Click handler for our "Click Me!" button. */
    // private onClicked = (): void => {
    //   // Increment state.numClicks, and pass the new value back to
    //   // Streamlit via `Streamlit.setComponentValue`.
    //   this.setState(
    //     prevState => ({ numClicks: prevState.numClicks + 1 }),
    //     () => Streamlit.setComponentValue(this.state.numClicks)
    //   )
    // }

// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).

export default withStreamlitConnection(MyComponent)
