import {
  Streamlit,
  withStreamlitConnection,
  ComponentProps,
  Theme,
} from "streamlit-component-lib"
import styled from "styled-components"
import React, { useState, useRef, useReducer, useCallback, useEffect, useMemo } from "react"
import AceEditor from "react-ace";
import ace from "ace-builds";
import { Editor } from "./editor";
import { Menu as ButtonMenu, Set as ButtonSet, customButton, buttonGroup, Info as InfoBar } from "./button-menu"

import "ace-builds/webpack-resolver";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";
import "ace-builds/src-noconflict/ext-prompt";
import "ace-builds/src-noconflict/ext-modelist";

interface CodeEditorProps extends ComponentProps {
  args: any
  width: number
  disabled: boolean
  theme?: Theme
}

const defaultOptions = {
  maxLines: 20,
  minLines: 20,
  fontFamily: '"Source Code Pro", monospace',
  cursorStyle: "smooth",
  displayIndentGuides: false,
  wrap: true,
  highlightActiveLine: true,
  showPrintMargin: false,
  showLineNumbers: false,
  foldStyle: "markbegin",
  autoScrollEditorIntoView: true,
  animatedScroll: true,
  fadeFoldWidgets: true,
}

const defaultEditorProps = {
  editorProps: {
    $blockScrolling: true
  }
}

const defaultProps = {
  cursorStart: 1,
  editorProps: defaultEditorProps,
  enableBasicAutocompletion: false,
  enableLiveAutocompletion: true,
  enableSnippets: true,
  focus: false,
  fontSize: 16,
  highlightActiveLine: true,
  navigateToFileEnd: true,
  placeholder: null,
  readOnly: false,
  scrollMargin: [15, 15, 0, 0],
  setOptions: defaultOptions,
  showGutter: true,
  showPrintMargin: false,
  style: {},
  tabSize: 4,
  width: "auto",
  debounceChangePeriod: 250,
}

const StyledCodeEditor = styled.div`
  border-radius: 8px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  :hover button {
    opacity: 1;
    transform: scale(1);
  }
  button.always-on {
    opacity: 1;
    transform: scale(1);
    transition: none;
  }
  :hover button svg {
        opacity: 1;
        transform: scale(1);
  }
  :hover button.with-icon span {
    opacity: 1;
    transform: scale(1);
  }
  button.always-on > span {
    opacity: 1;
    transform: scale(1);
    transition: none;
  }
  button.always-on > svg {
    opacity: 1;
    transform: scale(1);
    transition: none;
  }
  `;

const CodeEditor = ({ args, width, disabled, theme }: CodeEditorProps) => {

  //sets code to the initial value every time the component is rendered
  //to set the initial value once, use a function instead of a value.
  const [code, setCode] = useState(args['code']);  

  const aceEditor = useRef<AceEditor>(null);
  const infoTextRef = useRef<HTMLSpanElement>(null);

  var timeoutId: NodeJS.Timeout;

  useEffect(() => {
    console.log("CodeEditor Mounted>>>>>>>>>>>>>>>>");
    return () => {
      console.log("CodeEditor Unmounted<<<<<<<<<<<<<<<<");
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
    }
  }, []);

  const onChangeHandler = (newCode: string) => {
    setCode(newCode);
  }

  // commands is array containing functions that the editor calls when the bind key is pressed.
  const commands = { commands: [{
    name: 'submit', //name for the key binding.
    description: "Send 'submits response", //description of the command
    bindKey: { win: 'Ctrl-Enter', mac: 'Command-Enter' }, //key combination used for the command.
    exec: (editor: ace.Ace.Editor) => {
      console.log("submit keys pressed")
      Streamlit.setComponentValue({ type: "submit", code: editor.getValue() });
    }
  }, {
    name: 'save-state',
    description: "Save state",
    bindKey: { win: 'Ctrl-Alt-S', mac: 'Command-Alt-S' },
    exec: (editor: ace.Ace.Editor) => {
      console.log("save state keys pressed")
      setCode(editor.getValue());
    }
  }, {
    name: 'copyAll',
    description: "Copy all text to clipboard",
    exec: (editor: ace.Ace.Editor) => {
      unsecureCopyTextToClipboard(editor.getValue());
    }
  }, {
    name: 'reset',
    exec: () => {
      resetEditor();
    }
  },{
    name: 'infoMessage',
    description: "Display message in info bar",
    exec: (editor: ace.Ace.Editor, args: {text: string, timeout?: number, classToggle?: string} ) => {
      if(infoTextRef.current){
        infoTextRef.current.innerText = args.text;
        infoTextRef.current.classList.add(args.classToggle || "");
        if(args.timeout){
          console.log("calling set timeout")
          timeoutId = setTimeout(() => {
            console.log("settimeout called");
            infoTextRef.current?.classList.remove(args.classToggle || "");
          }, args.timeout);
        }

      }
    }
  }]};

  const execute = (command: string, args: any = "") => {
    const editor = aceEditor.current?.editor;
    if (editor) {
      if (!args) {
        editor.execCommand(command)
      }
      else if (typeof args === "number" || typeof args === "string") {
        console.log(command);
        console.log(args);
        editor.execCommand(command, args);
      }
      else if (typeof args === "object" && !Array.isArray(args)) {
        var containsNumsStr = true;
        Object.keys(args).forEach((key: any) => {
          containsNumsStr = typeof args[key] === "string" || typeof args[key] === "number";
        });
        if (Object.keys(args).length < 4 && containsNumsStr) {
          editor.execCommand(command, args);
        }
      }
      else {
        console.warn(`failed to execute "${command}" command!`);
      }
    }
  }

  const executeAll = (commands: any[], sendResponse = "") => {
    commands.forEach(singleCommand => {
      if (Array.isArray(singleCommand)) {
        console.log("here");
        typeof singleCommand[0] === "string" ? execute(singleCommand[0], singleCommand[1]) : console.warn("improper command format!");
      } else if (typeof singleCommand === "string") {
        execute(singleCommand);
      } else {
        console.warn("failed to execute command!");
      }
    });

    if (sendResponse) {
      respond(sendResponse);
    }
  }

  const respond = (responseType = "submit") => {
    console.log("respond called:" + responseType);
    Streamlit.setComponentValue({ type: responseType, code: code });
  }

  const resetEditor = () => {
    setCode(args['code']);
  }

  /**
  * This method for copying to clipboard uses the 
  * {@link https://developer.mozilla.org/en-US/docs/Web/API/Clipboard_API Clipboard API}
  * which makes use of the asynchronous clipboard read and write methods 
  * and requires that the user grant the web site or app permission to
  * access the clipboard. The latter can be an issue for streamlit because
  * it uses http instead of https. 
  * */
  const copyToClipboard = async (): Promise<void> => {
    try {
      await navigator.clipboard.writeText(code);
      console.log(code);
    } catch (err) {
      console.warn('Failed to copy securely -> reverting to unsecure copy');
      unsecureCopyToClipboard();
    }
  }

  /**
  * This method for copying to clipboard uses .execCommand method
  * which is depreciated but still supported by most browsers. This
  * is the same approach for copying that Streamlit's built in CodeBlock
  * component uses.
  **/
  const unsecureCopyToClipboard = () => {
    const textField = document.createElement('textarea');
    textField.value = aceEditor.current ? aceEditor.current.editor.getValue() : code;
    document.body.appendChild(textField);
    textField.select();
    document.execCommand('copy');
    textField.remove();
  }

  const unsecureCopyTextToClipboard = (text: string) => {
    const textField = document.createElement('textarea');
    textField.value = text;
    document.body.appendChild(textField);
    textField.select();
    document.execCommand('copy');
    textField.remove();
  }

  const resizeObserver = new ResizeObserver((entries: any) => {
    // If we know that the body will always fully contain our component (without cutting it off)
    // then we can use docuemnt.body height instead
    Streamlit.setFrameHeight((entries[0].contentBoxSize.blockSize ?? entries[0].contentRect.height)); 
  })

  const observe = (divElem: any) => {
    divElem ? resizeObserver.observe(divElem as HTMLDivElement) : resizeObserver.disconnect();
  }

  /**
   * This could also be memoized but I don't think it would be necessary because its not expensive.
   * @returns {string} themeProp
   */
  const themeChoice = () => {
    const isDarkTheme = theme? theme.base === "dark" : true;
    switch (args['theme']) {
      case "contrast":
        return isDarkTheme? "streamlit_light" : "streamlit_dark";
      case "light":
        return "streamlit_light";
      case "dark":
        return "streamlit_dark";
      case "default":
        return isDarkTheme? "streamlit_dark" : "streamlit_light";
      default:
        return isDarkTheme? "streamlit_dark" : "streamlit_light";
    }
  }

  const themeProp = themeChoice();
  const codeEditorProps = args["componentProps"];

  /**
   * This is the editor component that is rendered. It is memoized so that it is only re-rendered when
   * the args change. This is important because we should account for the possibility that the editor 
   * is being changed at any given moment. Forced re-renders due to the parent component re-rendering
   * can impact user experience and responsiveness.
   */
  const editor = useMemo(() => {
    console.log("EDITOR RENDERED");
    if(args['buttons'].length > 0) {
      args['buttons'].forEach((button: any) => {
          commands.commands = [...commands.commands, {
            name: (button.name as string).trim().replace(/\s+/g, '_') + '_button',
            description: "Execute '" + button.name + "' button command(s)",
            exec: () => {
            executeAll(button.commands, button.response);
          }}];
      });
    }
    const aceEditorProps = { ...defaultEditorProps, ...args['editorProps'] };
    const aceOptions = { ...defaultOptions, ...args['options'] };
    const partProps = { setOptions: aceOptions, editorProps: aceEditorProps };
    const aceProps = { ...defaultProps, ...partProps, ...args['props'] };
    return (
        <Editor editorRef={aceEditor} code={code} lang={args['lang']} theme={themeProp} shortcuts={args['shortcuts']} snippets={args['snippets']} commands={commands.commands} keybindings={args['keybindings']} props={aceProps} onChange={(value) => onChangeHandler(value)} />
      );
  }, [args.lang, args.shortcuts, args.snippets, args.props, args.editorProps, args.options, args.keybindings,args.buttons, themeProp]);

  const buttons = useMemo(() => {
    console.log("BUTTONS RENDERED");
    const customButtonTheme = theme ?? {};
    const customButtons = {buttons: (args['buttons'] ?? []) as customButton[], name: "customButtons"} as buttonGroup;
    return (
      < ButtonSet buttonGroup={customButtons} theme={customButtonTheme} themeProp={themeProp} executeAll={(commands, response) => executeAll(commands, response)} />
    );
  }, [args.buttons, theme, themeProp]);

  const menu = useMemo(() => {
    console.log("MENU RENDERED");
    const menuTheme = theme ?? {};
    return (
      <ButtonMenu menu={args['menu']} theme={menuTheme} themeProp={themeProp} executeAll={(commands, response) => executeAll(commands, response)} />
    );
  }, [args.menu, theme, themeProp]);

  const info = useMemo(() => {
    console.log("INFO RENDERED");
    const infoTheme = theme ?? {};
    return (
      <InfoBar infoRef={infoTextRef} info={args['info']} theme={infoTheme} themeProp={themeProp} />
    );
  }, [args.info, theme, themeProp]);

  console.log("PARENT RENDERED");

  return (
    <StyledCodeEditor ref={observe} style={{ ...(codeEditorProps.style ?? {}), width: "100%" }} css={codeEditorProps.css} className="streamlit_code-editor">
      {editor}
      {buttons}
      {menu}
      {info}
    </StyledCodeEditor>
  )
}

export default withStreamlitConnection(CodeEditor)
