import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import styled from "styled-components"
import React, { ReactNode } from "react"
import { Play as PlayIcon, Copy as CopyIcon } from "react-feather"
import AceEditor from "react-ace";
import ace, { UndoManager } from "ace-builds";

import "ace-builds/webpack-resolver";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-streamlit_dark";
import "ace-builds/src-noconflict/theme-streamlit_light";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox"
import { IAceEditor } from "react-ace/lib/types";
import "ace-builds/src-noconflict/ext-keybinding_menu";
//import "ace-builds/src-noconflict/keybinding-vscode";
//import { relative } from "path";
//import { Editor, CommandManager } from './streamlit-ace-build/ace';

interface State {
  code: string
  lang: string
  isFocused: boolean
  undoManager: object
  cursorPosition: object
}

const defaultOptions = {
  maxLines: 20,
  minLines: 20,
  fontFamily: '"Source Code Pro", monospace',
  cursorStyle: "smooth",
  displayIndentGuides: false,
  wrap: true,
  highlightActiveLine: true,
  showPrintMargin: false,
  showLineNumbers: false,
  foldStyle: "markbegin",
  autoScrollEditorIntoView: true,
  animatedScroll: true,
  fadeFoldWidgets: true,
}

const defaultEditorProps = {
  editorProps: {
    $blockScrolling: true
  }
}

const defaultProps = {
  cursorStart: 1,
  editorProps: defaultEditorProps,
  enableBasicAutocompletion: false,
  enableLiveAutocompletion: true,
  enableSnippets: true,
  focus: false,
  fontSize: 16,
  highlightActiveLine: true,
  navigateToFileEnd: true,
  placeholder: null,
  readOnly: false,
  scrollMargin: [15,15,0,0],
  setOptions: defaultOptions,
  showGutter: true,
  showPrintMargin: false,
  style: {},
  tabSize: 4,
  width:"auto",
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class CodeEditor extends StreamlitComponentBase<State> {
  private defaultUndoManager = new UndoManager();
  public state = {code: this.props.args['code'], lang: this.props.args['lang'], isFocused: false, cursorPosition: {row: 0, column: 1}, undoManager: this.defaultUndoManager}

  private renderedEditor = ace.require("ace/editor").Editor;

  private customButtons = [{
                              name: "console",
                              feather: "terminal",
                              commands: ["command"],
                              response: ""
                            },
                            {
                              name: "shortcuts",
                              feather: "type",
                              commands: ["showKeyboardShortcuts"],
                              response: ""
                            },
                            {
                              name: "removenewlines",
                              feather: "minimize-2",
                              commands: ["selectall", "toggleSplitSelectionIntoLines","gotolinestart", "backspace", ["insertstring", "\n"]],
                              response: ""
                            },
                            {
                              name: "save",
                              feather: "save",
                              commands: ["save-state"],
                              response: "saved"
                            }
                          ];

  // commands is array of key bindings.
  private commands = [{   
                      name: 'submit', //name for the key binding.
                      bindKey: {win: 'Ctrl-Enter', mac: 'Command-Enter'}, //key combination used for the command.
                      exec: (editor: ace.Ace.Editor) => {
                         console.log("submit keys pressed")

                         // IMPORTANT: The function `setState()` is required to make sure component state is saved before submit.
                         //            Otherwise changes to the editor and its contents will be lost.
                         this.setState({code: editor.getValue(), cursorPosition: editor.getCursorPosition(), undoManager: editor.getSession().getUndoManager()}, () => {Streamlit.setComponentValue({ type: "submit", code: editor.getValue()});}); 
                        }
                      },{
                      name: 'save-state',
                      bindKey: {win: 'Ctrl-Alt-S', mac: 'Command-Alt-S'},
                      exec: (editor: ace.Ace.Editor) => {
                         console.log("save state keys pressed")

                         this.setState({code: editor.getValue(), cursorPosition: editor.getCursorPosition(), undoManager: editor.getSession().getUndoManager(), lang: this.state.lang, isFocused: this.state.isFocused});
                        }
                      },{
                        name: "showKeyboardShortcuts",
                        bindKey: {win: "Ctrl-Alt-h", mac: "Command-Alt-h"},
                        exec: function(editor: ace.Ace.Editor) {
                            //ace.require("ace/ext/keybinding_menu").

                            // , function(module) {
                            //     module.init(editor);
                            //     editor.showKeyboardShortcuts()
                            // })
                            ace.config.loadModule("ace/ext/keybinding_menu", function(module) {
                              module.init(editor);
                              //editor.showKeyboardShortcuts()
                            })
                        }
                    }];

  public respond = (responseType = "submit") => {
    console.log("respond called");
    this.setState(this.state, () => {Streamlit.setComponentValue({ type: responseType, code: this.state.code});});
  }

  /**
   * This method for copying to clipboard uses the 
   * {@link https://developer.mozilla.org/en-US/docs/Web/API/Clipboard_API Clipboard API}
   * which makes use of the asynchronous clipboard read and write methods 
   * and requires that the user grant the web site or app permission to
   * access the clipboard. The latter can be an issue for streamlit because
   * it uses http instead of https. 
   * */
  public copyToClipboard = async (): Promise<void> => {
    try {
      await navigator.clipboard.writeText(this.state.code);
      console.log(this.state.code);
    } catch (err) {
      console.warn('Failed to copy securely -> reverting to unsecure copy');
      this.unsecureCopyToClipboard();
    }
  }

  /**
   * This method for copying to clipboard uses .execCommand method
   * which is depreciated but still supported by most browsers. This
   * is the same approach for copying that Streamlit's built in CodeBlock
   * component uses.
   **/
  private unsecureCopyToClipboard = () => {
    const textField = document.createElement('textarea');
    textField.value = this.state.code;
    document.body.appendChild(textField);
    textField.select();
    document.execCommand('copy');
    textField.remove();
  }

  private unsecureCopyTextToClipboard = (text: string) => {
    const textField = document.createElement('textarea');
    textField.value = text;
    document.body.appendChild(textField);
    textField.select();
    document.execCommand('copy');
    textField.remove();
  }

  /**
   * This function takes as input either a snippetText string that is expected
   * to be already formatted like the text in a SnippetFile or a dictionary or 
   * and array of dictionaries. For the first case, it just returns the input.
   * For the second and third case, a (SnippetFile format) string is constructed
   * from the dict(s) and returned. Learn more about snippets {@link  here}
   * @param snippets snippets to be converted to a single snippetText string
   * @returns {string} snippetText
   */
  private createSnippets = (snippets: string | object | [object]) : string => {
    return( typeof snippets === "string" ? snippets : (Array.isArray(snippets) ? snippets : [snippets]).map(({ name, code }) =>
        ([
            'snippet ' + name,
            code.split('\n')
                .map((c: string) => '\t' + c)
                .join('\n'),
        ].join('\n'))
    ).join('\n'))
  }

  private handleOnBlur = (event: any, editor: IAceEditor) => {
    console.log("~~onBlur~~");           

    // IMPORTANT!! Calls CodeEditor's `setState` function whenever user clicks anywhere
    // outside CodeEditor component but not inside. Because onBlur events inside CodeEditor
    // can result in changes you want to keep, calling setState before those changes are 
    // processed is not wise. 
    console.log(event)
    if(event.relatedTarget || event.sourceCapabilities){
      this.state = {code: editor?.getValue() || this.state.code, cursorPosition: editor?.getCursorPosition() || this.state.cursorPosition, undoManager: editor?.getSession().getUndoManager() || this.state.undoManager, lang: this.state.lang, isFocused: this.state.isFocused}
    } else {
      this.setState({code: editor?.getValue() || this.state.code, cursorPosition: editor?.getCursorPosition() || this.state.cursorPosition, undoManager: editor?.getSession().getUndoManager() || this.state.undoManager, lang: this.state.lang, isFocused: this.state.isFocused});
    }
  }

