import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
  ComponentProps,
  Theme,
} from "streamlit-component-lib"
import styled from "styled-components"
import React, { useState, useRef, useReducer, useCallback, useEffect } from "react"
import * as Icons from "react-feather"
import AceEditor, { IAceEditorProps } from "react-ace";
import ace, { UndoManager } from "ace-builds";

import "ace-builds/webpack-resolver";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";
import "ace-builds/src-noconflict/ext-prompt";
import "ace-builds/src-noconflict/ext-modelist";
import { IAceEditor } from "react-ace/lib/types";
import "ace-builds/src-noconflict/ext-keybinding_menu";
import ReactAce from "react-ace/lib/ace";


interface CodeEditorProps extends ComponentProps {
  args: any
  width: number
  disabled: boolean
  theme?: Theme
}

// interface State {
//   code: string
//   lang: string
//   isFocused: boolean
//   undoManager: object
//   cursorPosition: object
// }

interface customInfoText {
  name: string,
  class?: string,
  style?: object,
  theme?: object
}

interface infoBar {
  name?: string,
  style?: object,
  info?: customInfoText[]
}

interface customButton {
  name: string,
  feather?: string,
  iconSize?: number,
  primary?: boolean,
  hasText?: boolean,
  showWithIcon?: boolean,
  alwaysOn?: boolean,
  commands?: any[],
  response?: string,
  class?: string,
  style?: object,
  theme?: object
}

interface buttonGroup {
  name: string,
  style?: object,
  buttons: customButton[]
}

interface menu {
  menuProps?: object,
  groups?: buttonGroup[]
}

const defaultOptions = {
  maxLines: 20,
  minLines: 20,
  fontFamily: '"Source Code Pro", monospace',
  cursorStyle: "smooth",
  displayIndentGuides: false,
  wrap: true,
  highlightActiveLine: true,
  showPrintMargin: false,
  showLineNumbers: false,
  foldStyle: "markbegin",
  autoScrollEditorIntoView: true,
  animatedScroll: true,
  fadeFoldWidgets: true,
}

const defaultEditorProps = {
  editorProps: {
    $blockScrolling: true
  }
}

const defaultProps = {
  cursorStart: 1,
  editorProps: defaultEditorProps,
  enableBasicAutocompletion: false,
  enableLiveAutocompletion: true,
  enableSnippets: true,
  focus: false,
  fontSize: 16,
  highlightActiveLine: true,
  navigateToFileEnd: true,
  placeholder: null,
  readOnly: false,
  scrollMargin: [15,15,0,0],
  setOptions: defaultOptions,
  showGutter: true,
  showPrintMargin: false,
  style: {},
  tabSize: 4,
  width:"auto",
}

const StyledCodeEditor = styled.div`
border-radius: 8px;
overflow: hidden;
display: flex;
flex-direction: column;
:hover button {
  opacity: 1;
  transform: scale(1);
}
button.always-on {
  opacity: 1;
  transform: scale(1);
  transition: none;
}
:hover button svg {
      opacity: 1;
      transform: scale(1);
}
:hover button.with-icon span {
  opacity: 1;
  transform: scale(1);
}
button.always-on > span {
  opacity: 1;
  transform: scale(1);
  transition: none;
}
button.always-on > svg {
  opacity: 1;
  transform: scale(1);
  transition: none;
}
`;

const Editor = ({args, code, keyHandler, lang}: any) => {
  const [value, setValue] = useState(code as string);
  const [keyboardHandler, setKeyboardHandler] = useState(keyHandler as string);
  const [mode, setMode] = useState(lang as string);

  const handleOnFocus = (event: any, editor: IAceEditor) => {
    console.log("~~onFocus~~");
    console.log("~~~~~~~~~~~~~");
  }

  const handleOnBlur = (event: any, editor: IAceEditor) => {
    console.log("~~onBlur~~");
    console.log("~~~~~~~~~~~~~");
  }

  const handleBeforeLoad = (Ace: typeof ace) => {
    console.log("~before load~");
    console.log("~~~~~~~~~~~~~");
  }

  const handleOnLoad = (editor: ace.Ace.Editor) => {
    console.log("~on load~");
    console.log("~~~~~~~~~~~~~")

  }

  const handleOnChange = (text: string, event?: any) => {
      if(!(value === text)){
        setValue(value);
      } 
  }

  useEffect(() => {
    console.log("mounting============================")
    return () => {
      console.log("===================>unmounting");
    }
  }, []);

  const aceEditorProps = {...defaultEditorProps, ...args['editorProps']};
  const aceOptions = {...defaultOptions, ...args['options']}
  const partProps = {setOptions: aceOptions, editorProps: aceEditorProps}
  const aceProps = {...defaultProps, ...partProps, ...args['props']}

  return (<AceEditor
    name="REACT_ACE_EDITOR"
    mode={mode}
    value={value}
    keyboardHandler={keyboardHandler}
    onChange={handleOnChange}
    onBeforeLoad={handleBeforeLoad}
    onLoad={handleOnLoad}
    onFocus={handleOnFocus}
    onBlur={handleOnBlur}
    {...aceProps}/>)
}

const CodeEditor = ({args, width, disabled, theme}: CodeEditorProps) => {

  const resizeObserver = new ResizeObserver((entries: any) => {
    Streamlit.setFrameHeight(entries[0].contentBoxSize.blockSize ?? entries[0].contentRect.height);
  })

  const observe = (divElem: any) => {
    divElem? resizeObserver.observe(divElem as HTMLDivElement) : resizeObserver.disconnect();
  }

  const codeEditorProps = args["rootProps"];

    return (
      <StyledCodeEditor ref={observe} style={{...codeEditorProps, width: "100%"}} className="streamlit_code-editor">
          <Editor args={{...args}} code={args['code'] as string} lang={args['lang'] as string} keyHandler={args['shortcuts'] as string} />
      </StyledCodeEditor>
      )
  }

export default withStreamlitConnection(CodeEditor)
