import styled, { ThemeProvider } from "styled-components"
import * as Icons from "react-feather"

export interface customInfoText {
  name: string,
  class?: string,
  style?: object,
  theme?: object
}

export interface infoBar {
  name?: string,
  style?: object,
  info?: customInfoText[]
}

export interface customButton {
  name: string,
  feather?: string,
  iconSize?: number,
  primary?: boolean,
  hasText?: boolean,
  showWithIcon?: boolean,
  alwaysOn?: boolean,
  commands?: any[],
  response?: string,
  class?: string,
  style?: object,
  theme?: object
}

export interface buttonGroup {
  name: string,
  style?: object,
  buttons: customButton[]
}

export interface menu {
  menuProps?: object,
  groups?: buttonGroup[]
}

export const StyledDiv = styled.div``;
export const StyledRegSpan = styled.span``;

export const StyledSpan = styled.span`
  height: 2.5rem;
  line-height: 2.5rem;
  margin: 0px 0.4rem 0.15rem 0.4rem;
  opacity: 0;
  transform: scale(0);
  transition: opacity 300ms 150ms, transform 300ms 150ms;
`;

export const StyledButton = styled.button<{primary?: boolean, themeProp: string, theme: object, style: object}>`
  border: none;
  border-radius: 5px;
  background: none;
  height: 2.5rem;
  line-height: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  position: absolute;
  transform: scale(0);
  transition: opacity 20ms 300ms, transform 20ms 300ms;
  top: ${props => props.style.top || ""};
  left: ${props => props.style.left || ""};
  right: ${props => props.style.right || ""};
  bottom: ${props => props.style.bottom || ""};
  z-index: 101;
  :focus {
    outline: none;
  }
  span{
    color: ${props => props.themeProp === "streamlit_dark"? "rgba(250,250,250,0.6)": "rgba(0,0,0,0.5)"};
    transform: scale(0);
    transform-origin: right;
    transition: opacity 300ms 150ms, transform 300ms 150ms;
  }
  :hover span {
    color: ${props => props.primary? props.theme.primaryColor : props.themeProp === "streamlit_dark"? "rgb(250,250,250)" : "rgb(49, 51, 63)"};
    opacity: 1;
    transform: scale(1);
  }
  svg {
    stroke: ${props => props.themeProp === "streamlit_dark"? "rgba(250,250,250,0.6)": "rgba(0,0,0,0.5)"};
    transform: scale(0);
    transition: opacity 300ms 150ms, transform 300ms 150ms;
  }
  :hover svg {
    stroke: ${props => props.primary? props.theme.primaryColor : props.themeProp === "streamlit_dark"? "rgb(250,250,250)" : "rgb(49, 51, 63)"};
  }
`;

export type iconKey = keyof typeof Icons;

export const creatIcon = (name: iconKey, size = 16) => {
  const CustomIcon = Icons[name];
  return <CustomIcon size={`${size}`} />;
}

export type CustomInfoBar = {
  theme: object,
  themeProp: string,
  info: infoBar,
  infoRef: any,
}

export const Info = ({info, theme, themeProp, infoRef}: CustomInfoBar) => {

  console.log("Info bar is rendering!!!!!!!!!!!!!!");

  return (
    <ThemeProvider theme={theme}>
      {(Object.keys(info).length === 0) ? `` : <StyledDiv key="info_bar" className={"custom_info_bar " + (info.name? info.name : "")} style={info.style ?? {}}>
        {<StyledRegSpan ref={infoRef} key="code_editor_info_message" className="code_editor-info message">
          </StyledRegSpan>}
        {(info.info || []).map((info: customInfoText)=>(
          <StyledRegSpan key={"info_" + info.name} className={info.class ?? ``} style={info.style ?? {}}>
            {info.name}
          </StyledRegSpan>
        ))}
      </StyledDiv>}
    </ThemeProvider>);
}

export type CustomButtonSet = {
    theme: object,
    themeProp: string,
    buttonGroup: buttonGroup,
    executeAll: (commands: any[], sendResponse?: string) => void 
  }

export const  Set = ({buttonGroup, executeAll, theme, themeProp}: CustomButtonSet) => {

    const execute = (commands: any[], sendResponse?: string) => {
      if(commands || sendResponse){
        executeAll(commands, sendResponse);
      } else { 
        console.log("No commands or response to execute");
      }
    }

    console.log("Button set is rendering!!!!!!!!!!!!!!");
    return (
        <ThemeProvider theme={theme}>
          {buttonGroup.buttons.map( (button, index) => (
          <StyledButton 
            primary={button.primary? button.primary : false}
            key={buttonGroup.name + "_" + button.name} 
            className={(button.class? button.class: "" ) + (button.alwaysOn? " always-on" : "") + (button.showWithIcon? " with-icon" : "")}
            themeProp={themeProp}
            style={button.style? button.style : {}} 
            onClick={() => execute(button.commands ?? [], button.response)}>
              {(button.hasText && button.name)? <StyledSpan >{button.name}</StyledSpan> : ``}
              {button.feather? creatIcon(button.feather as iconKey || "Copy", button.iconSize) : ""}
          </StyledButton>
        ))}
      </ThemeProvider>
    )
  }

export type CustomMenu = {
    menu : menu,
    theme: object,
    themeProp: string,
    executeAll: (commands: any[], sendResponse?: string) => void
  }

export const Menu = ({menu, executeAll, theme, themeProp}: CustomMenu) => {

    const execute = (commands: any[], sendResponse?: string) => {
      if(commands || sendResponse){
        executeAll(commands, sendResponse);
      } else { 
        console.log("No commands or response to execute");
      }
    }

    return (
      <ThemeProvider theme={theme}>
        {(Object.keys(menu).length === 0 )? "" : <StyledDiv key="menu_bar" className="custom_menu" style={menu.menuProps ?? {}}>
          {!menu.groups? `` : menu.groups.map((group: buttonGroup, index) => (
            <StyledDiv key={"group_" + group.name + index} className={"menu_group " + group.name} style={group.style || {}}>
                {group.buttons.map((button: customButton, index)=>(
                  <StyledButton 
                  primary={button.primary ?? false}
                  key={group.name + "_" + button.name}
                  className={(button.class ?? "" ) + (button.alwaysOn? " always-on" : "")+ (button.showWithIcon? " with-icon" : "")}
                  themeProp={themeProp}
                  theme={button.theme ?? {}} 
                  style={button.style ?? {}}
                  onClick={() => execute(button.commands ?? [], button.response)}>
                    {(button.hasText && button.name)? <StyledSpan>{button.name}</StyledSpan> : ``}
                    {button.feather? creatIcon(button.feather as iconKey || "Copy", button.iconSize) : ""}
                  </StyledButton>
                ))}
            </StyledDiv>
          ))}
        </StyledDiv>
        }</ThemeProvider>
    )
  }
