import { useEffect} from "react"
import AceEditor, { ICommandBindKey } from "react-ace";
import ace from "ace-builds";

import "ace-builds/webpack-resolver";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";
import "ace-builds/src-noconflict/ext-prompt";
import "ace-builds/src-noconflict/ext-modelist";
import "ace-builds/src-noconflict/ext-keybinding_menu";

export interface KeyBinding {
    bindkey: string | object,
    name: string
}

export interface EditorKeyBindings {
  commands?: KeyBinding[],
  completer?: KeyBinding[]
}

export type EditorProps = {
    code: string,
    lang: string,
    theme: string,
    shortcuts: string,
    props: any,
    editorRef: any,
    snippetString: string,
    commands: object[],
    keybindingString: string,
    onChange: (value: string, event?: any) => void 
  }

export const Editor = ({ lang, theme, shortcuts, props, snippetString, commands, keybindingString, editorRef, code, onChange }: EditorProps ) => {

  useEffect(() => {
    console.log("editor mounted");
    return () => {
      console.log("editor unmounted");
    }
  }, []);

  useEffect(() => {
    console.log("~~~~~ useEffect inside Editor ~~~~~");
    //console.log(aceEditor.current?.editor);
    if(editorRef.current){
      console.log(editorRef.current.editor.getSession().$modeId);
      console.log(editorRef.current.editor.commands);

      ace.require('ace/autocomplete').Autocomplete.for(editorRef.current.editor);
      const keybindings = JSON.parse(keybindingString) as EditorKeyBindings;
      console.log(keybindings);
      if(keybindings.commands && keybindings.commands.length > 0){
        const bindkeySelector = editorRef.current.editor.commands.platform as string;
        keybindings.commands.forEach((binding) => {
            // (editorRef.current.editor as ace.Ace.Editor).commands.bindKey(binding.bindkey, binding.name, undefined);
            // console.log(typeof (editorRef.current.editor.commands as ace.Ace.CommandManager).bindKey)

            if(binding.name && typeof binding.name === "string"){
              if (JSON.stringify(editorRef.current.editor.commands.commands[binding.name].bindKey) !== JSON.stringify(binding.bindkey)) {
                const newCommand = {...editorRef.current.editor.commands.commands[binding.name]};
                newCommand.bindKey = binding.bindkey;
                commands.push(newCommand);
              }
            }

            // if(typeof binding.bindkey === "string" || (typeof binding.bindkey === "object" && (binding.bindkey as any)[bindkeySelector])){
            //   editorRef.current.editor.commands.bindKey(binding.bindkey, binding.name);
            // }
            // else if(binding.name && typeof binding.name === "string") {
            //   editorRef.current.editor.commands.removeCommand(binding.name, true);
            // }      
        });
      }
      console.log(editorRef.current.editor);
      // if(keybindings.completer && keybindings.completer.length > 0){
      //   const bindkeySelector = editorRef.current.editor.completer.keyboardHandler.platform as string;
      //   keybindings.completer.forEach((binding) => {
      //       if(typeof binding.bindkey === "string" || (typeof binding.bindkey === "object" && (binding.bindkey as any)[bindkeySelector])){
      //         editorRef.current.editor.completer.keyboardHandler.bindKey(binding.bindkey, binding.name);
      //       }
      //       else if(binding.name && typeof binding.name === "string") {
      //         editorRef.current.editor.completer.keyboardHandler.removeCommand(binding.name, true);
      //       }    
      //   });
      // }
      ace.require("ace/ext/keybinding_menu").init(editorRef.current.editor);
      console.log(editorRef.current.editor);
      const snippetManager = ace.require('ace/snippets').snippetManager;
      const snippets = JSON.parse(snippetString) as object;
      for (const [snippetsLang, snippetsAddRemove] of Object.entries(snippets)){
        console.log("new snippets")
        console.log(snippetsLang);
        console.log(snippetsAddRemove);
        if(snippetsAddRemove[0]){
          snippetManager.register(snippetManager.parseSnippetFile(snippetsAddRemove[0], snippetsLang), snippetsLang)
          console.log(snippetManager);
        } else {
          console.log("Cannot register snippets: check format of snippets");
        }
        if(snippetsAddRemove[1]){
          console.log("snippets to remove")
          console.log(snippetManager.parseSnippetFile(snippetsAddRemove[1], snippetsLang));

          console.log(snippetManager.parseSnippetFile(snippetManager.files['ace/mode/' + snippetsLang].snippetText, snippetsLang));
          snippetManager.unregister(snippetManager.parseSnippetFile(snippetsAddRemove[1], snippetsLang), snippetsLang)
          console.log(snippetManager);
        } else {
          console.log("Cannot unregister snippets: check format of snippets");
        }
      }
    } else {
      console.log("No editor to register/unregister snippets to");
    }
  }, [snippetString, keybindingString]);

  return (
          <AceEditor
          ref={editorRef}
          name="REACT_ACE_EDITOR"
          mode={lang}
          theme={theme}
          value={code}
          keyboardHandler={shortcuts}
          commands={commands}
          onChange={onChange}
          {...props} />
  );
};
