import { useEffect} from "react"
import AceEditor from "react-ace";
import ace from "ace-builds";

import "ace-builds/webpack-resolver";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";
import "ace-builds/src-noconflict/ext-prompt";
import "ace-builds/src-noconflict/ext-modelist";
import "ace-builds/src-noconflict/ext-keybinding_menu";

export interface CommandBinding {
    bindkey: string,
    name: string
}

export type EditorProps = {
    code: string,
    lang: string,
    theme: string,
    shortcuts: string,
    props: any,
    editorRef: any,
    snippetText: string[],
    commands: object,
    keybindings: CommandBinding[],
    onChange: (value: string, event?: any) => void 
  }

export const Editor = ({ lang, theme, shortcuts, props, snippetText, commands, keybindings, editorRef, code, onChange }: EditorProps ) => {


      useEffect(() => {
        console.log("~~~~~ useEffect inside Editor ~~~~~");
        //console.log(aceEditor.current?.editor);
        if(editorRef.current){
            console.log(keybindings);
            console.log(editorRef.current?.editor.commands);

            if(keybindings.length > 0){
                keybindings.forEach((binding) => {
                    editorRef.current?.editor.commands.bindkey(binding.bindkey, binding.name);      
                });
            }
            // (editorRef.current as AceEditor).editor.commands.bindKeys({})

            ace.require("ace/ext/keybinding_menu").init(editorRef.current?.editor);
            console.log(editorRef.current?.editor);

            if(snippetText[0]){
              const snippetManager = ace.require('ace/snippets').snippetManager;

              //// The function getMode() returns an Ace.SyntaxId object that
              //// has no way to get or access its $id variable. So the following
              //// doesnt work:
              //const id = editor.session.getMode().$id;

              //// The current workaround used here is to recognize that when the 
              //// editorRef mode prop is set with a string (ex: mode="python"), it  
              //// constructs the mode id as follows: setMode(`ace/mode/${mode}`)
              //const id = `ace/mode/${this.state.lang}`;

              snippetManager.register(snippetManager.parseSnippetFile(snippetText[0], lang), lang)
              console.log(snippetManager);
            } else {
              console.log("Cannot register snippets: check format of snippets");
            }
            if(snippetText[1]){
              const snippetManager = ace.require('ace/snippets').snippetManager;

              snippetManager.unregister(snippetManager.parseSnippetFile(snippetText[1], lang), lang)
              console.log(snippetManager);
            } else {
              console.log("Cannot unregister snippets: check format of snippets");
            }
        } else {
          console.log("No editor to register/unregister snippets to");
        }
        return () => {console.log("Editor unmounted*********")}
      }, [snippetText[0], snippetText[1]]);

    return (
            <AceEditor
            ref={editorRef}
            name="REACT_ACE_EDITOR"
            mode={lang}
            theme={theme}
            value={code}
            keyboardHandler={shortcuts}
            commands={commands}
            onChange={onChange}
            {...props} />
    );
  };
